
public class SwapTwoNumbers {
	
	public static int x=10;
	public static int y=20;
	public static void swap() {
		int temp;
		temp=x;
		x=y;
		y=temp;
		System.out.println("x="+x+","+"y="+y);
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		swap();

	}

}
